CREATE DATABASE TestePraticoTPC;USE TestePraticoTPC;CREATE TABLE [dbo].[Usuarios] (    [Id]       INT IDENTITY(1,1) NOT NULL,    [Nome]     NVARCHAR(100) NOT NULL,    [Email]    NVARCHAR(100) NOT NULL,    [Senha]    NVARCHAR(255) NOT NULL,    CONSTRAINT [PK_Usuarios] PRIMARY KEY CLUSTERED ([Id] ASC),    CONSTRAINT [UQ_Usuarios_Email] UNIQUE NONCLUSTERED ([Email] ASC));CREATE TABLE [dbo].[Tarefas] (    [Id]         INT IDENTITY(1,1) NOT NULL,    [Titulo]     NVARCHAR(100) NOT NULL,    [Descricao]  NVARCHAR(500) NULL,    [Status]     INT NOT NULL DEFAULT (0),    [UsuarioId]  INT NOT NULL,    CONSTRAINT [PK_Tarefas] PRIMARY KEY CLUSTERED ([Id] ASC),    CONSTRAINT [FK_Tarefas_Usuarios] FOREIGN KEY ([UsuarioId]) REFERENCES [dbo].[Usuarios] ([Id]));